const i=e=>{const t=e.currentTarget,o=new URL(t.href),s=o.hash.substring(1),n=document.getElementById(s);window.location.pathname===o.pathname&&n&&(e.preventDefault(),n.scrollIntoView({behavior:"smooth"}))};document.querySelectorAll('a[href*="#"]').forEach(e=>{e.addEventListener("click",i)});window.addEventListener("load",()=>{if(window.location.hash){const e=document.getElementById(window.location.hash.substring(1));e&&setTimeout(()=>{e.scrollIntoView({behavior:"smooth"})},100)}});const r=new IntersectionObserver(e=>{e.forEach(t=>{t.isIntersecting?t.target.classList.add("is-visible"):t.target.classList.remove("is-visible")})},{threshold:.1});document.querySelectorAll(".fade-in-section").forEach(e=>{r.observe(e)});
